import React, { useState } from "react";
import { Helmet } from "react-helmet";
import { Button, Text, Input, Img } from "../../components";
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function SignUpPage() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const user = {
      username,
      email,
      password
    };

    try {
      const response = await axios.post("http://localhost:8080/api/user/createUser", user);

      if (response.status === 200) {
        console.log("User created successfully");
        history.push("/firsttimesigninenterprofileinformation");
      } else {
        console.error("Failed to create user");
      }
    } catch (error) {
      console.error("Network error or server is not responding:", error);
    }
  };

  return (
    <>
      <Helmet>
        <title>Sign Up - Create Your Account Today</title>
        <meta
          name="description"
          content="Join us by creating an account. Ensure your password includes 8 characters, an uppercase, a lowercase, a number, and a special character for security."
        />
      </Helmet>

      <div className="w-full bg-blue_gray-100_01">
        <div className="flex items-start justify-between gap-5 bg-blue_gray-800 p-11 md:flex-col md:p-5">
          <Img
            src="images/img_logo.png"
            alt="house1one_one"
            className="ml-[54px] mt-28 h-[376px] w-[376px] object-cover md:ml-0 md:w-full"
          />

          <form onSubmit={handleSubmit} className="mr-[31px] flex h-[632px] w-[46%] flex-col items-start justify-end bg-[url(/public/images/img_group_18.svg)] bg-cover bg-no-repeat p-[7px] md:mr-0 md:h-auto md:w-full md:p-5">
            <div className="mb-[5px] mt-[55px] flex w-[92%] flex-col items-end md:w-full">
              <Text size="lg" as="p" className="text-center !text-blue_gray-800_01">
                Create an account
              </Text>
              <Input
                shape="square"
                type="text"
                name="username"
                placeholder="Enter your username"
                className="mt-[17px] w-[93%] sm:pr-5"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
              <Input
                shape="square"
                type="email"
                name="email"
                placeholder="Enter an email"
                className="mt-4 w-[93%] sm:pr-5"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <Input
                shape="square"
                type="password"
                name="password"
                placeholder="Enter password"
                className="mt-3 w-[93%] sm:pr-5"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <Text size="s" as="p" className="mt-3 w-[46%] self-start text-center !text-blue_gray-800_7a md:w-full">
                Password must contain: <br />8 characters<br />1 uppercase<br />1 lowercase<br />1 number<br />1 special character
              </Text>
              <Button type="submit" shape="round" className="mr-[111px] mt-5 min-w-[199px] !rounded-[22px] md:mr-0 sm:px-5">
                Continue
              </Button>
            </div>
          </form>
        </div>
      </div>
    </>
  );
}
