import React, { useState, useEffect } from 'react';
import axios from "axios";

const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

export default function MealPlan() {
    const [recipes, setRecipes] = useState({});

    const getRecipesForDay = (day) => {
        axios.get('http://localhost:8080/api/recipe/randRec')
            .then(response => {
                setRecipes(prevState => ({
                    ...prevState,
                    [day]: response.data // Store recipes for the specific day
                }));
            })
            .catch(error => {
                console.error(error);
            });
    }

    useEffect(() => {
        // Fetch recipes for each day of the week
        daysOfWeek.forEach(day => {
            getRecipesForDay(day);
        });
    }, []);

    return (
        <div className="container mx-auto mt-10 p-4" style={{ backgroundColor: "#a3b18a"}}>
            <h1 className="text-3xl font-bold text-white mb-6">Weekly Meal Plan</h1>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {daysOfWeek.map((day) => (
                    <div key={day} className="bg-white rounded shadow-md p-4">
                        <h2 className="text-lg font-bold mb-4">{day}</h2>
                        <div>
                            {recipes[day]?.map((meal, index) => (
                                <div key={`${day}-${index}`} className="flex justify-between items-center border-b border-gray-200 py-2">
                                    <div>{meal.name}</div>
                                    <div className="text-sm text-gray-500">{meal.mealType}</div>
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};
